import { createStore } from 'vuex'
import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default createStore({
  state: {
  isLoggedIn: false
  },
  mutations: {
  login(state) {
  state.isLoggedIn = true
  },
  logout(state) {
  state.isLoggedIn = false
  }
  },
  getters: {
  },
  actions: {
  },
  modules: {
  }
})
