import { createStore } from 'vuex';
import Vue from 'vue';

const store = createStore({
  state() {
    return {
      isLoggedIn: false
    }
  },
  mutations: {
    login(state) {
      state.isLoggedIn = true
    },
    logout(state) {
      state.isLoggedIn = false
    }
  },
  getters: {},
  actions: {},
  modules: {}
})

export default store
